import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [file, setFile] = useState(null);
  const [imageUrl, setImageUrl] = useState(null);

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('file', file);
    // const response = await axios.post('http://localhost:5000/api/process-csv', formData)
    // .then(response => {
    //   console.log(response.data);
    //   // Display the image in the frontend
    // })
    // .catch(error => console.error(error));
    const response = axios.post('http://localhost:5000/api/process-csv', formData)
  .then(response => {
    const imageElement = document.createElement('img');
    imageElement.src = `data:image/png;base64,${response.data.image_data}`;
    document.body.appendChild(imageElement);
  })
  .catch(error => console.error(error));

    // setImageUrl(response.data.imageUrl);

  // .catch(error => console.error(error));

    console.log(imageUrl);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input type="file" accept=".csv" onChange={handleFileChange} />
        <button type="submit">Submit</button>
      </form>
      {imageUrl && <img src={imageUrl} />}
    </div>
  );
}

export default App;
